(101, 'Income Tax', '', '', '', '<ul type="disc"><li>Consultancy on various intricate matters pertaining to Income tax.</li><li>Effective tax management, tax structuring and advisory services.</li><li>Tax Planning for Corporates and others.</li><li>Designing / restructuring salary structure to minimise tax burden.</li><li>Obtaining Advance tax Rulings.</li><li>Obtaining No Objection Certificates from Income tax department.</li><li>Obtaining PAN for assessees, employees etc.</li><li>Advance tax estimation and deposit.</li><li>Assessing the liability towards deferred taxes.</li><li>Providing regular updates on amendments, circulars, notifications &amp; judgments.</li><li>Filing Income Tax and Wealth Tax returns for all kinds of assessees.</li><li>Filing Income tax returns for employees of corporate clients.</li><li>Liaison with Income tax department for rectification, assessment, obtaining refunds etc.</li><li>Expertise in complicated direct tax assessments.</li><li>Filing and pleading appeals under various provisions of IT Act.</li><li>Special expertise in search, seizure and prosecution litigation.</li><li>Advice on future tax implications in respect of the potential acquisition.</li><li>Opinions on the various Double Tax Avoidance Agreement related issues.</li><li>Settlement of various issues raised under FEMA.</li></ul>', '', '', '', '', '', '', '', '', '', '', '2014-06-27 06:57:42', '0000-00-00 00:00:00', '', '', 0, 'active', '', 'IncomeTax', 0),
(102, 'Service Tax', '', '', '', '<ul type="disc"><li>Registration under Service tax.</li><li>Consultancy for maintenance of proper records.</li><li>Consultancy for proper accounting for Service tax.</li><li>Consultancy on various issues relating to Service Tax.</li><li>Compilation of data of Cenvat credit.</li><li>Assistance in availment of notified Abatements.</li><li>Utilization and availment of service tax credit in accordance with the Service Tax Credit Rules, 2002.</li><li>Computation of Service tax payable.</li><li>Preparation and filing Returns.</li><li>Getting assessments done.</li></ul>', '', '', '', '', '', '', '', '', '', '', '2014-06-27 06:57:42', '0000-00-00 00:00:00', '', '', 0, 'active', '', 'ServiceTax', 0),
(103, 'Corporate Services', '', '', '', '<ul type="disc"><li>Incorporation of company.</li><li>Consultancy on Company Law matters.</li><li>Planning for Mergers, Acquisitions, De-mergers, and Corporate re-organizations.</li><li>Filing of annual returns and various forms, documents.</li><li>Clause 49 review for compliance with fiscal, corporate and tax laws.</li><li>Secretarial Matters including share transfers.</li><li>Maintenance of Statutory records.</li><li>Consultancy on Public/Rights/Bonus Issue of shares.</li><li>Change of Name, Objects, Registered Office, etc.</li></ul>', '', '', '', '', '', '', '', '', '', '', '2014-06-27 07:08:38', '0000-00-00 00:00:00', '', '', 0, 'active', '', 'CorporateServices', 0),
